import React, { useRef, useState } from 'react';
import axios from 'axios';
import '../styles/App.css';
import '../styles/Intro.css';
import img1 from '../images/picture1.png';
import img2 from '../images/picture2.png';
import img3 from '../images/picture3.png';

function Intro() {
    const fileInputRef = useRef(null);
    const webcamRef = useRef(null);
    const [selectedFile, setSelectedFile] = useState(null);
    const [isWebcamOpen, setIsWebcamOpen] = useState(false);
    const [imageSrc, setImageSrc] = useState(null);

    // Function to trigger file input click when the container is clicked
    const handleContainerClick = () => {
        fileInputRef.current.click();
    };

    // Handle file change event
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setSelectedFile(file);
        uploadFile(file); // Upload the file when selected
    };

    // Function to upload file to backend (Flask API)
    const uploadFile = async (file) => {
        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            alert('File uploaded successfully!');
            console.log(response.data);
        } catch (err) {
            console.error('Error uploading file:', err);
            alert('File upload failed');
        }
    };

    // Function to open webcam
    const openWebcam = async () => {
        setIsWebcamOpen(true);
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        webcamRef.current.srcObject = stream;
    };

    // Function to capture photo from webcam
    const capturePhoto = () => {
        const canvas = document.createElement('canvas');
        const context = canvas.getContext('2d');
        canvas.width = webcamRef.current.videoWidth;
        canvas.height = webcamRef.current.videoHeight;
        context.drawImage(webcamRef.current, 0, 0, canvas.width, canvas.height);
        const photo = canvas.toDataURL('image/jpeg'); // Convert to data URL
        setImageSrc(photo);
        setIsWebcamOpen(false);
        uploadPhoto(photo);
    };

    // Function to upload captured photo
    const uploadPhoto = async (photo) => {
        const response = await fetch(photo);
        const blob = await response.blob();
        const file = new File([blob], 'captured_photo.jpg', { type: 'image/jpeg' });
        uploadFile(file);
    };

    return (
        <div className='app-body-comp'>
            <div className='intro-container'>
                <h1 className='intro-title'>Getting Started</h1>
            </div>
            <form className='steps-container'>
                <div className='step-box' onClick={handleContainerClick} style={{ cursor: 'pointer' }}>
                    <p className='intro-title step-title'>Upload Image</p>
                    <img className='step-img' src={img2} alt="Upload" />
                </div>

                {/* Hidden file input */}
                <input
                    type="file"
                    ref={fileInputRef}
                    style={{ display: 'none' }}
                    onChange={handleFileChange}
                />

                {/* Button to open webcam */}
                <button type="button" onClick={openWebcam}>Open Webcam</button>

                {/* Webcam video element */}
                {isWebcamOpen && (
                    <div>
                        <video ref={webcamRef} autoPlay style={{ width: '100%', height: 'auto' }} />
                        <button type="button" onClick={capturePhoto}>Capture Photo</button>
                    </div>
                )}

                {/* Show captured photo */}
                {imageSrc && <img src={imageSrc} alt="Captured" style={{ width: '100%', height: 'auto' }} />}
            </form>
        </div>
    );
}

export default Intro;
